
public class ClaseFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//int i = 0 -> Inicializacion
		// i <= 10 -> Condicion para seguir ejecutando el for
		// i++ -> Al terminar la iteracion realizo una operación
		
		System.out.println("Ejemplo FOR");
		for (int i = 11; i > 0; i--) {
			System.out.println(i);
			
		}
		
		System.out.println("Ejemplo WHILE");
		int i = 11;
		
		while (i > 0) {
			System.out.println(i);
			
			i--;
		}
		
	}

}
